from typing import List, Tuple

def calc_max_and_mean(values: List[float]) -> Tuple[float, float]:
    """数値リストから最大値と平均値を返す"""
    if not values:
        raise ValueError("空のリストです")

    max_val = max(values)
    mean_val = sum(values) / len(values)
    return max_val, mean_val


